function escondeTodasAbas(){var e=document.querySelectorAll("[data-tab-id]");for(let t=0;t<e.length;t++)e[t].classList.remove("shows__list--is-active")}document.addEventListener("DOMContentLoaded",function(){var e=document.querySelectorAll("[data-tab-button]");for(let t=0;t<e.length;t++)e[t].addEventListener("click",function(t){t=t.target.dataset.tabButton,t=document.querySelector(`[data-tab-id=${t}]`);escondeTodasAbas(),t.classList.add("shows__list--is-active")})});